import React, { useEffect, useRef, useState } from 'react';
import { useParams } from 'react-router-dom';
import io from 'socket.io-client';
import Toolbar from './Toolbar';
import UserCursors from './UserCursors';
import './Whiteboard.css';

const socket = io('http://localhost:5000');

function Whiteboard() {
  const { roomId } = useParams();
  const canvasRef = useRef(null);
  const ctxRef = useRef(null);
  const lastPos = useRef({ x: 0, y: 0 }); // ✅ Fixed position storage
  const [isDrawing, setIsDrawing] = useState(false);
  const [strokeColor, setStrokeColor] = useState('black');
  const [strokeWidth, setStrokeWidth] = useState(2);
  const [userCursors, setUserCursors] = useState({});

  useEffect(() => {
    const canvas = canvasRef.current;
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
    const ctx = canvas.getContext('2d');
    ctx.lineCap = 'round';
    ctx.lineJoin = 'round';
    ctxRef.current = ctx;

    socket.emit('join-room', roomId);
 socket.on('draw-move', ({ stroke }) => {
  const { x0, y0, x1, y1, color, width } = stroke;
  drawLine(x0, y0, x1, y1, color, width);
});


    socket.on('draw', ({ x0, y0, x1, y1, color, width }) => {
      drawLine(x0, y0, x1, y1, color, width);
    });

    socket.on('clear-canvas', () => {
      clearCanvas(false);
    });

    socket.on('cursor-move', ({ id, x, y }) => {
      setUserCursors((prev) => ({
        ...prev,
        [id]: { x, y, timestamp: Date.now() },
      }));
    });

    const interval = setInterval(() => {
      const now = Date.now();
      setUserCursors((prev) =>
        Object.fromEntries(
          Object.entries(prev).filter(([_, v]) => now - v.timestamp < 3000)
        )
      );
    }, 3000);

    return () => clearInterval(interval);
  }, [roomId]);

  const startDrawing = ({ nativeEvent }) => {
    const { offsetX, offsetY } = nativeEvent;
    setIsDrawing(true);
    lastPos.current = { x: offsetX, y: offsetY }; // ✅ Set starting point
  };

  const finishDrawing = () => {
    setIsDrawing(false);
  };

  const draw = ({ nativeEvent }) => {
    if (!isDrawing) return;
    const { offsetX, offsetY } = nativeEvent;
    const { x: lastX, y: lastY } = lastPos.current;

    drawLine(lastX, lastY, offsetX, offsetY, strokeColor, strokeWidth);

    socket.emit('draw', {
      roomId,
      x0: lastX,
      y0: lastY,
      x1: offsetX,
      y1: offsetY,
      color: strokeColor,
      width: strokeWidth,
    });

    lastPos.current = { x: offsetX, y: offsetY }; // ✅ Update last position
  };

  const drawLine = (x0, y0, x1, y1, color, width) => {
    const ctx = ctxRef.current;
    ctx.strokeStyle = color;
    ctx.lineWidth = width;
    ctx.beginPath();
    ctx.moveTo(x0, y0);
    ctx.lineTo(x1, y1);
    ctx.stroke();
    ctx.closePath();
  };

  const clearCanvas = (emit = true) => {
    const canvas = canvasRef.current;
    ctxRef.current.clearRect(0, 0, canvas.width, canvas.height);
    if (emit) socket.emit('clear-canvas', roomId);
  };

  const handleMouseMove = (e) => {
    socket.emit('cursor-move', {
      roomId,
      id: socket.id,
      x: e.clientX,
      y: e.clientY,
    });
  };

  return (
    <>
      <Toolbar
        color={strokeColor}
        setColor={setStrokeColor}
        width={strokeWidth}
        setWidth={setStrokeWidth}
        onClear={() => clearCanvas(true)}
      />
      <canvas
        ref={canvasRef}
        onMouseDown={startDrawing}
        onMouseUp={finishDrawing}
        onMouseMove={(e) => {
          draw(e);
          handleMouseMove(e);
        }}
        className="whiteboard-canvas"
      />
      <UserCursors cursors={userCursors} />
    </>
  );
}

export default Whiteboard;
